import React, { useState } from 'react';
import { ethers } from "ethers";
import './App.css';
import styles from './index.css';
import { PoapDisplay } from './PoapDisplay';

function App() {
  const [identifier, setIdentifier] = useState('');
  const [poaps, setPoaps] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const provider = new ethers.providers.JsonRpcProvider('https://mainnet.infura.io/v3/8e73f4d8cb144502bebc7cd9fed06f23');

  const resolveENS = async (name) => {
    if (identifier.includes('@')) {
      // address = emailToEnsMapping[identifier] || null;
      address = null
      if (address) {
        address = await provider.resolveName(address);
        if (!address) {
          throw new Error('Could not resolve ENS name');
        }
      } else {
        throw new Error('No ENS name found for this email');
      }
    } else if (address.endsWith('.eth')) {
      address = await provider.resolveName(address);
      if (!address) {
        throw new Error('Could not resolve ENS name');
      }
    } else {
      address = name
    }
    return address
  }

  const fetchPOAPs = async (address) => {
    setLoading(true);
    setError('');
    setPoaps([]);

    const thisAddress = await provider.resolveName("poap.eth");
    console.log(thisAddress)
    try {
      const response = await fetch('https://public.compass.poap.tech/v1/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
            query {
              poaps(where: { collector_address: { _eq: "${address.toLowerCase()}" } }) {
                id
                drop_id
                collector_address
                drop {
                  image_url
                  name
                }
              }
            }
          `,
        }),
      });
      const result = await response.json();
      console.log(result)
      if (result.data.poaps.length === 0) {
        throw new Error('No POAPs found for this identifier');
      }
      setPoaps(result.data.poaps);
    } catch (err) {
      setError(err.message || 'Invalid identifier');
    }
    setLoading(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (identifier.trim() === '') {
      setError('Please enter a valid identifier (address, ENS, or email)');
      return;
    }

    try {
      let address = await resolveENS(identifier)
      fetchPOAPs(address);
    } catch (err) {
      setError(err.message || 'Invalid identifier');
    }
  };

  const handleReset = () => {
    setIdentifier('');
    setPoaps([]);
    setError('');
  };

  return (
    <div className="container">
      <form onSubmit={handleSubmit} >
        <input
          type="text"
          value={identifier}
          onChange={(e) => setIdentifier(e.target.value)}
          placeholder="Enter address, ENS, or email"
          className={styles.input}
        />
        <button type="submit" className={styles.button}>Search</button>
        <button type="button" className={styles.button} onClick={handleReset}>Reset</button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p className={styles.error}>{error}</p>}
      <div className="container">
        {poaps.length > 0 && <PoapDisplay poaps={poaps} />}
      </div>
    </div>
  );
}

export default App;
