import React, { useState } from 'react';
// import { ethers } from 'ethers';  // For resolving ENS names
import styles from './index.css';

const POAPCollection = () => {
  const [identifier, setIdentifier] = useState('');
  const [poaps, setPoaps] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchPOAPs = async (address) => {
    setLoading(true);
    setError('');
    setPoaps([]);

    try {
      const response = await fetch('https://public.compass.poap.tech/v1/graphql', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
            query {
              poaps(where: { collector_address: { _eq: "${address.toLowerCase()}" } }) {
                id
                drop_id
                collector_address
                drop {
                  image_url
                }
              }
            }
          `,
        }),
      });
      const result = await response.json();
      console.log(result)
      if (result.data.poaps.length === 0) {
        throw new Error('No POAPs found for this identifier');
      }
      setPoaps(result.data.poaps);
    } catch (err) {
      setError(err.message || 'Invalid identifier');
    }
    setLoading(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (identifier.trim() === '') {
      setError('Please enter a valid identifier (address, ENS, or email)');
      return;
    }

    try {
      let address = identifier;

      // If identifier ends with .eth, treat it as ENS and resolve to address
      // if (identifier.endsWith('.eth')) {
      //   const provider = new ethers.providers.JsonRpcProvider(`https://mainnet.infura.io/v3/YOUR_INFURA_PROJECT_ID`);
      //   address = await provider.resolveName(identifier);
      //   if (!address) {
      //     throw new Error('Could not resolve ENS name');
      //   }
      // }

      fetchPOAPs(address);
    } catch (err) {
      setError(err.message || 'Invalid identifier');
    }
  };

  const handleReset = () => {
    setIdentifier('');
    setPoaps([]);
    setError('');
  };

  return (
    <div className={styles.container}>
      <form onSubmit={handleSubmit} className={styles.form}>
        <input
          type="text"
          value={identifier}
          onChange={(e) => setIdentifier(e.target.value)}
          placeholder="Enter address, ENS, or email"
          className={styles.input}
        />
        <button type="submit" className={styles.button}>Search</button>
        <button type="button" className={styles.button} onClick={handleReset}>Reset</button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.collection}>
        {poaps.length > 0 && (
          <div>
            <h3>User's POAP Collection</h3>
            <div className={styles.grid}>
              {poaps.map((poap) => (
                <div key={poap.tokenId} className={styles.poapItem}>
                  <a href={`https://poap.gallery/drop/${poap.drop_id}`} target="_blank" rel="noopener noreferrer">
                    <img src={`${poap}?size=small`} alt={poap.id} className={styles.poapImage} />
                  </a>
                  <p>{poap.id}</p>
                  <p>Drop ID: {poap.id}</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default POAPCollection;
