import React, { useState } from 'react';
import styles from './index.css';

const CollectionSearch = () => {
  const [identifier, setIdentifier] = useState('');
  const [poaps, setPoaps] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const fetchPOAPs = async (id) => {
    setLoading(true);
    setError('');
    setPoaps([]);

    try {
      const response = await fetch('https://api.thegraph.com/subgraphs/name/poap-xyz/poap', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: `
            query {
              accounts(where: {id: "${id.toLowerCase()}"}){
                tokens {
                  id
                  event {
                    id
                    name
                    image_url
                  }
                }
              }
            }
          `,
        }),
      });
      const result = await response.json();
      if (result.data.accounts.length === 0) {
        throw new Error('No POAPs found for this identifier');
      }
      setPoaps(result.data.accounts[0].tokens);
    } catch (err) {
      setError(err.message || 'Invalid identifier');
    }
    setLoading(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (identifier.trim() === '') {
      setError('Please enter a valid identifier (address, ENS, or email)');
      return;
    }
    fetchPOAPs(identifier);
  };

  const handleReset = () => {
    setIdentifier('');
    setPoaps([]);
    setError('');
  };

  return (
    <div className={styles.container}>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={identifier}
          onChange={(e) => setIdentifier(e.target.value)}
          placeholder="Enter address, ENS, or email"
          className={styles.input}
        />
        <button type="submit" className={styles.button}>Search</button>
        <button type="button" className={styles.button} onClick={handleReset}>Reset</button>
      </form>

      {loading && <p>Loading...</p>}
      {error && <p className={styles.error}>{error}</p>}
      <div className={styles.collection}>
        {poaps.length > 0 && (
          <div>
            <h3>User's POAP Collection</h3>
            <ul>
              {poaps.map((poap) => (
                <li key={poap.id}>
                  <img src={poap.event.image_url} alt={poap.event.name} className={styles.poapImage} />
                  <p>{poap.event.name}</p>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default CollectionSearch;
